    let box_axes = [
        Vec3 { x: 1.0, y: 0.0, z: 0.0 },
        Vec3 { x: 0.0, y: 1.0, z: 0.0 },
        Vec3 { x: 0.0, y: 0.0, z: 1.0 },
    ];

    let v0 = &tri.v0;
    let v1 = &tri.v1;
    let v2 = &tri.v2;

    let f0 = v1.sub(v0);
    let f1 = v2.sub(v1);
    let f2 = v0.sub(v2);

