        box_axes[0],
        box_axes[1],
        box_axes[2],
        f0.cross(&box_axes[0]),
        f0.cross(&box_axes[1]),
        f0.cross(&box_axes[2]),
        f1.cross(&box_axes[0]),
        f1.cross(&box_axes[1]),
        f1.cross(&box_axes[2]),
        f2.cross(&box_axes[0]),
        f2.cross(&box_axes[1]),
        f2.cross(&box_axes[2]),
