    pub aabb: AABB,
    pub children: Option<[Arc<Mutex<OctreeNode>>; 8]>,
    pub faces: Vec<usize>, // This contains the face indices
}

impl OctreeNode {
    pub fn new(aabb: AABB) -> Self {
        OctreeNode {
            aabb,
            children: None,
            faces: Vec::new(),
        }
    }

    // Function to subdivide the node into 8 children
    fn subdivide(&mut self) {
        let bounding_boxes = self.aabb.split();

        let children = [
            Arc::new(Mutex::new(OctreeNode::new(bounding_boxes[0].clone()))),
            Arc::new(Mutex::new(OctreeNode::new(bounding_boxes[1].clone()))),
            Arc::new(Mutex::new(OctreeNode::new(bounding_boxes[2].clone()))),
            Arc::new(Mutex::new(OctreeNode::new(bounding_boxes[3].clone()))),
            Arc::new(Mutex::new(OctreeNode::new(bounding_boxes[4].clone()))),
            Arc::new(Mutex::new(OctreeNode::new(bounding_boxes[5].clone()))),
            Arc::new(Mutex::new(OctreeNode::new(bounding_boxes[6].clone()))),
            Arc::new(Mutex::new(OctreeNode::new(bounding_boxes[7].clone()))),
        ];

        self.children = Some(children);
    }


    pub fn insert<'a>(&'a mut self, leaves: &mut Arc<Mutex<Vec<&'a OctreeNode>>>, face_index: usize, vertices: &[Vector3<f32>; 3], max_depth: usize, depth: usize) {
        if self.aabb.size().min() <= 1.0 {
            self.faces.push(face_index);
            leaves.lock().unwrap().push(self);
            return;
        }

        // subdivide
        if self.children.is_none() {
            self.subdivide();
        }

        match self.children {
            None => {}
            Some(ref mut children) => {
                for child in children.iter() {
                    let mut ch = child.lock().unwrap();
                    if triangle_aabb_intersection(&vertices, &ch.aabb) {
                        ch.insert(leaves, face_index, vertices, max_depth, depth + 1);
                    }
                }
            }
        }
    }
}
```

### Explanation:
- I changed `&child.lock().unwrap().aabb` to `&ch.aabb` to ensure that we aren't creating a second lock within the condition.
- This prevents the `ch` variable from being dropped prematurely, maintaining its lifetime appropriately.

This change minimizes the lifetime issues by ensuring that we hold onto the lock for as long as needed within each iteration.

```rust
use std::sync::{Arc, Mutex};
use nalgebra::{Vector3};
use rayon::prelude::*;
use crate::aabb::{triangle_aabb_intersection, AABB};


