```rust
use std::sync::{Arc, Mutex};
use nalgebra::Vector3;
use raylib::camera::Camera3D;
use raylib::drawing::RaylibDraw;
use raylib::init;
use raylib::prelude::{CameraMode, RaylibDraw3D, RaylibDrawHandle, RaylibMode3D, RaylibMode3DExt};
use crate::octree::OctreeNode;

use rayon::prelude::*;
use crate::aabb::AABB;

mod loader;
mod octree;
mod vec_ops;
mod aabb;

fn draw_octree(d: &mut RaylibMode3D<RaylibDrawHandle>, node: &OctreeNode) {
    // draw only if leaf node
    if !node.faces.is_empty() {
        let size = node.aabb.size();
        d.draw_cube(
            raylib::prelude::Vector3::new(node.aabb.min.x, node.aabb.min.y, node.aabb.min.z),
            size.x, size.y, size.z,
            raylib::prelude::Color::new(0, 0, 255, 255),
        );
    }

    match node.children {
        Some(ref children) => {
            for child in children.iter() {
                draw_octree(d, &child);
            }
        }
        None => {}
    }
}

fn main() {
    let model = loader::load().unwrap();
    let mesh = &model.meshes[0];

    let bmin = mesh.aabb.min.min().floor() - 1.0;
    let mut bmax = mesh.aabb.max.max().ceil() + 1.0;
    if (bmax - bmin) != 0.0 {
        bmax += 1.0;
    }

    println!("Range: [{}, {}]", bmin, bmax);

    let mut root = Box::new(OctreeNode::new(AABB {
        min: Vector3::<f32>::new(bmin, bmin, bmin),
        max: Vector3::<f32>::new(bmax, bmax, bmax),
    }));

    let leaves = Arc::new(Mutex::new(vec![]));

    for index in &mesh.indices {
        root.insert(
            &mut Arc::clone(&leaves),
            0,
            &[
                mesh.vertices[index[0] as usize],
                mesh.vertices[index[1] as usize],
                mesh.vertices[index[2] as usize],
            ],
            6,
            0,
        )
    }

